def compute_abs_int(x: int) -> int:
    """Compute absolute value of integer."""
    if x < 0:
        return -x
    return x

def compute_abs_float(x: float) -> float:
    """Compute absolute value of float."""
    if x < 0.0:
        return -x
    return x

def sum_three(a: int, b: int, c: int) -> int:
    """Sum three integers."""
    return a + b + c

def product_three(a: int, b: int, c: int) -> int:
    """Multiply three integers."""
    return a * b * c

def max_two(a: int, b: int) -> int:
    """Return maximum of two integers."""
    if a > b:
        return a
    return b

def min_two(a: int, b: int) -> int:
    """Return minimum of two integers."""
    if a < b:
        return a
    return b

def is_positive(x: int) -> bool:
    """Check if integer is positive."""
    return x > 0

def is_even(x: int) -> bool:
    """Check if integer is even."""
    return x % 2 == 0

def factorial(n: int) -> int:
    """Compute factorial of n."""
    if n <= 1:
        return 1
    return n * factorial(n - 1)

def fibonacci(n: int) -> int:
    """Compute nth Fibonacci number."""
    if n <= 1:
        return n
    return fibonacci(n - 1) + fibonacci(n - 2)

def clamp(x: int, low: int, high: int) -> int:
    """Clamp x to range [low, high]."""
    if x < low:
        return low
    if x > high:
        return high
    return x

def sign(x: int) -> int:
    """Return sign of x: -1, 0, or 1."""
    if x < 0:
        return -1
    if x > 0:
        return 1
    return 0

def gcd(a: int, b: int) -> int:
    """Compute greatest common divisor."""
    while b != 0:
        a, b = b, a % b
    return a

def lcm(a: int, b: int) -> int:
    """Compute least common multiple."""
    def gcd(x: int, y: int) -> int:
        while y != 0:
            x, y = y, x % y
        return x
    return abs(a * b) // gcd(a, b)

def is_prime(n: int) -> bool:
    """Check if n is prime."""
    if n < 2:
        return False
    if n == 2:
        return True
    if n % 2 == 0:
        return False
    i = 3
    while i * i <= n:
        if n % i == 0:
            return False
        i += 2
    return True

def power(base: int, exp: int) -> int:
    """Compute base raised to exp."""
    if exp == 0:
        return 1
    result = 1
    for _ in range(exp):
        result *= base
    return result

def sum_range(start: int, end: int) -> int:
    """Sum integers from start to end (exclusive)."""
    total = 0
    for i in range(start, end):
        total += i
    return total

def count_digits(n: int) -> int:
    """Count number of digits in n."""
    if n == 0:
        return 1
    count = 0
    n = abs(n)
    while n > 0:
        count += 1
        n //= 10
    return count

def sum_of_squares(n: int) -> int:
    """Sum of squares from 1 to n."""
    total = 0
    for i in range(1, n + 1):
        total += i * i
    return total

def squares(n: int) -> list:
    """Generate squares using list comprehension."""
    return [x * x for x in range(n)]

def evens(nums: list) -> list:
    """Filter even numbers using list comprehension."""
    return [x for x in nums if x % 2 == 0]

def square_dict(n: int) -> dict:
    """Create dict mapping numbers to squares."""
    return {x: x * x for x in range(n)}

def safe_divide(a: float, b: float) -> float:
    """Safely divide with error handling."""
    try:
        return a / b
    except ZeroDivisionError:
        return 0.0

def parse_int_safe(s: str) -> int:
    """Parse string to int with error handling."""
    try:
        return int(s)
    except ValueError:
        return -1

def get_item_safe(lst: list, index: int) -> int:
    """Get list item with bounds checking."""
    try:
        return lst[index]
    except IndexError:
        return -1

class Point:
    """2D point with distance calculation."""
    def __init__(self, x: float, y: float):
        self.x = x
        self.y = y

    def distance_from_origin(self) -> float:
        return (self.x ** 2 + self.y ** 2) ** 0.5

class Counter:
    """Simple counter with increment/decrement."""
    def __init__(self):
        self.value = 0

    def increment(self):
        self.value += 1

    def decrement(self):
        self.value -= 1

    def get(self) -> int:
        return self.value

class Shape:
    """Abstract shape base class."""
    def area(self) -> float:
        raise NotImplementedError

class Rectangle(Shape):
    def __init__(self, width: float, height: float):
        self.width = width
        self.height = height

    def area(self) -> float:
        return self.width * self.height

from dataclasses import dataclass

@dataclass
class Point3D:
    """3D point using dataclass."""
    x: float
    y: float
    z: float

    def magnitude(self) -> float:
        return (self.x**2 + self.y**2 + self.z**2) ** 0.5

class Temperature:
    """Temperature with property decorator."""
    def __init__(self, celsius: float):
        self._celsius = celsius

    @property
    def celsius(self) -> float:
        return self._celsius

    @property
    def fahrenheit(self) -> float:
        return self._celsius * 9/5 + 32

    @celsius.setter
    def celsius(self, value: float):
        self._celsius = value
